import React, { useState } from "react";

function PaymentGateway() {
  const [paymentMethod, setPaymentMethod] = useState("");
  const [googlePayType, setGooglePayType] = useState("phone");
  const [modal, showModal] = useState(false);
  const [amount, setAmount] = useState("");
  const [paymentStatus, setPaymentStatus] = useState(""); // "", "processing", "confirmed"
  const [paymentComplete, setPaymentComplete] = useState(false);

  const handlePaymentChange = (e) => {
    setPaymentMethod(e.target.value);
  };

  const handleGooglePayTypeChange = (e) => {
    setGooglePayType(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    showModal(true);
  };

  const closeModal = (e) => {
    e.preventDefault();
    showModal(false);
    setPaymentStatus(""); // reset status when modal closes
  };

  const handleConfirmPayment = (e) => {
    e.preventDefault();
    // Start processing payment: wait for a few seconds, then show a green tick,
    // and finally change to the Payment Gateway screen.
    setPaymentStatus("processing");
    setTimeout(() => {
      setPaymentStatus("confirmed");
      setTimeout(() => {
        setPaymentComplete(true);
        showModal(false);
      }, 1500);
    }, 2000);
  };

  // If payment is complete, display the Payment Gateway view
  if (paymentComplete) {
    return (
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          minHeight: "100vh",
          backgroundColor: "#f5f6f7",
        }}
      >
        <h2>Payment Gateway</h2>
        <p>
          Your payment of ${amount} via {paymentMethod} has been successfully processed.
        </p>
      </div>
    );
  }

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        minHeight: "100vh",
        backgroundColor: "#f5f6f7",
      }}
    >
      <div
        style={{
          width: "400px",
          padding: "20px",
          borderRadius: "8px",
          background: "white",
          boxShadow: "0px 0px 10px rgba(0,0,0,0.1)",
        }}
      >
        <h2 style={{ textAlign: "center", marginBottom: "20px" }}>Payment Gateway</h2>
        <form onSubmit={handleSubmit}>
          {/* Payment method selection */}
          <div style={{ marginBottom: "20px" }}>
            <label
              htmlFor="paymentMethod"
              style={{ display: "block", marginBottom: "8px" }}
            >
              Choose Payment Method:
            </label>
            <select
              id="paymentMethod"
              value={paymentMethod}
              onChange={handlePaymentChange}
              style={{ width: "100%", padding: "8px", borderRadius: "4px" }}
              required
            >
              <option value="">Select</option>
              <option value="creditCard">Credit Card</option>
              <option value="paypal">PayPal</option>
              <option value="googlePay">Google Pay</option>
            </select>
          </div>

          {/* Conditional rendering based on the selected payment method */}
          {paymentMethod === "creditCard" && (
            <div style={{ marginBottom: "20px" }}>
              <h3>Credit Card Details</h3>
              <label htmlFor="merchantName" style={{ display: "block", marginBottom: "4px" }}>
                Merchant Name
              </label>
              <input
                type="text"
                id="merchantName"
                placeholder="John Doe"
                style={{ width: "100%", padding: "8px", borderRadius: "4px" }}
                required
              />
              <div style={{ marginBottom: "10px" }}>
                <label htmlFor="cardNumber" style={{ display: "block", marginBottom: "4px" }}>
                  Card Number:
                </label>
                <input
                  type="text"
                  id="cardNumber"
                  placeholder="1234 5678 9012 3456"
                  pattern="\d{16}"
                  style={{ width: "100%", padding: "8px", borderRadius: "4px" }}
                  required
                />
              </div>
              <div style={{ marginBottom: "10px" }}>
                <label htmlFor="expiry" style={{ display: "block", marginBottom: "4px" }}>
                  Expiry Date:
                </label>
                <input
                  type="text"
                  id="expiry"
                  placeholder="MM/YY"
                  pattern="\d{2}/\d{2}"
                  style={{ width: "100%", padding: "8px", borderRadius: "4px" }}
                  required
                />
              </div>
              <div style={{ marginBottom: "10px" }}>
                <label htmlFor="cvv" style={{ display: "block", marginBottom: "4px" }}>
                  CVV:
                </label>
                <input
                  type="text"
                  id="cvv"
                  placeholder="123"
                  pattern="\d{3}"
                  style={{ width: "100%", padding: "8px", borderRadius: "4px" }}
                  required
                />
              </div>
            </div>
          )}

          {paymentMethod === "paypal" && (
            <div style={{ marginBottom: "20px" }}>
              <h3>PayPal Details</h3>
              <label htmlFor="merchantName" style={{ display: "block", marginBottom: "4px" }}>
                Merchant Name
              </label>
              <input
                type="text"
                id="merchantName"
                placeholder="John Doe"
                style={{ width: "100%", padding: "8px", borderRadius: "4px" }}
                required
              />
              <div style={{ marginBottom: "10px" }}>
                <label htmlFor="paypalEmail" style={{ display: "block", marginBottom: "4px" }}>
                  PayPal Email:
                </label>
                <input
                  type="email"
                  id="paypalEmail"
                  placeholder="you@example.com"
                  style={{ width: "100%", padding: "8px", borderRadius: "4px" }}
                  required
                />
              </div>
            </div>
          )}

          {paymentMethod === "googlePay" && (
            <div style={{ marginBottom: "20px" }}>
              <h3>Google Pay Details</h3>
              <label htmlFor="merchantName" style={{ display: "block", marginBottom: "4px" }}>
                Merchant Name
              </label>
              <input
                type="text"
                id="merchantName"
                placeholder="John Doe"
                style={{ width: "100%", padding: "8px", borderRadius: "4px" }}
                required
              />
              <div style={{ marginBottom: "10px" }}>
                <label htmlFor="googlePayType" style={{ display: "block", marginBottom: "4px" }}>
                  Login Type:
                </label>
                <select
                  id="googlePayType"
                  value={googlePayType}
                  onChange={handleGooglePayTypeChange}
                  style={{ width: "100%", padding: "8px", borderRadius: "4px" }}
                >
                  <option value="phone">Phone Number</option>
                  <option value="id">Google Pay ID</option>
                </select>
              </div>
              {googlePayType === "phone" ? (
                <div style={{ marginBottom: "10px" }}>
                  <label htmlFor="googlePayPhone" style={{ display: "block", marginBottom: "4px" }}>
                    Phone Number:
                  </label>
                  <input
                    type="tel"
                    id="googlePayPhone"
                    placeholder="+1 234 567 8900"
                    style={{ width: "100%", padding: "8px", borderRadius: "4px" }}
                    required
                  />
                </div>
              ) : (
                <div style={{ marginBottom: "10px" }}>
                  <label htmlFor="googlePayId" style={{ display: "block", marginBottom: "4px" }}>
                    Google Pay ID:
                  </label>
                  <input
                    type="text"
                    id="googlePayId"
                    placeholder="johndoe@okicici"
                    style={{ width: "100%", padding: "8px", borderRadius: "4px" }}
                    required
                  />
                </div>
              )}
            </div>
          )}

          <button
            type="submit"
            style={{
              width: "100%",
              padding: "10px",
              backgroundColor: "#4267B2",
              color: "white",
              border: "none",
              borderRadius: "4px",
              fontSize: "16px",
              cursor: "pointer",
              marginTop: "10px",
            }}
          >
            Proceed to Pay
          </button>
        </form>
      </div>
      {/* Modal for entering amount and confirming payment */}
      {modal && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: "rgba(0,0,0,0.5)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000,
          }}
        >
          <div
            style={{
              background: "#fff",
              padding: "20px",
              borderRadius: "8px",
              width: "300px",
              textAlign: "center",
            }}
          >
            {paymentStatus === "" && (
              <>
                <h3>Enter Payment Amount</h3>
                <input
                  type="number"
                  value={amount}
                  onChange={(e) => setAmount(e.target.value)}
                  placeholder="Enter amount"
                  style={{
                    width: "100%",
                    padding: "8px",
                    margin: "10px 0",
                    borderRadius: "4px",
                  }}
                />
                <div>
                  <button
                    onClick={handleConfirmPayment}
                    style={{
                      padding: "8px 16px",
                      backgroundColor: "#28a745",
                      color: "white",
                      border: "none",
                      borderRadius: "4px",
                      cursor: "pointer",
                    }}
                  >
                    Confirm Payment
                  </button>
                  <button
                    onClick={closeModal}
                    style={{
                      padding: "8px 16px",
                      marginLeft: "10px",
                      backgroundColor: "#dc3545",
                      color: "white",
                      border: "none",
                      borderRadius: "4px",
                      cursor: "pointer",
                    }}
                  >
                    Cancel
                  </button>
                </div>
              </>
            )}
            {paymentStatus === "processing" && (
              <div>
                <h3>Processing Payment...</h3>
              </div>
            )}
            {paymentStatus === "confirmed" && (
              <div>
                <h3 style={{ color: "green" }}>âœ… Payment Successful!</h3>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default PaymentGateway;
