import React, { useState } from 'react';
import axios from 'axios';
import moment from 'moment';

const Transactions = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [dates, setDates] = useState({
    start_date: moment().subtract(30, 'days').format('YYYY-MM-DD'),
    end_date: moment().format('YYYY-MM-DD')
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await axios.post('http://localhost:3001/api/transactions', dates);
      setTransactions(response.data);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch transactions');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>PayPal Transactions</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Start Date:
          <input
            type="date"
            value={dates.start_date}
            onChange={(e) => setDates({ ...dates, start_date: e.target.value })}
          />
        </label>
        <label>
          End Date:
          <input
            type="date"
            value={dates.end_date}
            onChange={(e) => setDates({ ...dates, end_date: e.target.value })}
          />
        </label>
        <button type="submit" disabled={loading}>
          {loading ? 'Loading...' : 'Fetch Transactions'}
        </button>
      </form>

      {error && <div style={{ color: 'red' }}>{error}</div>}

      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Transaction ID</th>
            <th>Amount</th>
            <th>Payer</th>
            <th>Status</th>
            <th>Type</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map((t) => (
            <tr key={t.transaction_info.transaction_id}>
              <td>{new Date(t.transaction_info.transaction_initiation_date).toLocaleDateString()}</td>
              <td>{t.transaction_info.transaction_id}</td>
              <td>
                {t.transaction_info.transaction_amount.value} 
                {t.transaction_info.transaction_amount.currency_code}
              </td>
              <td>{t.payer_info.payer_name?.alternate_full_name}</td>
              <td>{t.transaction_info.transaction_status}</td>
              <td>{t.transaction_info.transaction_subject}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Transactions;
